import React, { Component } from 'react';
import './Login.css';
import { Button, Input, Form, FormText, FormGroup, Label, Alert } from 'reactstrap';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';



class Login extends Component {
    state = {
        email: '',
        password: '',
        valid: false,
        error: ''
    }
    onNameChange = (e) => {
        const name = e.target.value;
        this.setState(() => ({ name }))
        this.setState({ valid: true })
    }
    onSurnameChange = (e) => {
        const surname = e.target.value;
        this.setState(() => ({ surname }))
    }
    onEmailChange = (e) => {
        const email = e.target.value;
        this.setState(() => ({ email }))
    }
    onPasswordChange = (e) => {
        const password = e.target.value;
        this.setState(() => ({ password }))
    }

    onSubmit = (e) => {
        e.preventDefault();

        if ( !this.state.email || !this.state.password) {

            this.setState({ error: "Lütfen tüm alanları doldurunuz..." })
        }


    }
    
    render() {
        return (
            <div className="login-container">
                <div className="form-content">
                <h1>Login</h1>
                <Form>
                    <FormGroup>
                        <Label for="exampleEmail">
                            Email
                        </Label>
                        <Input
                            id="exampleEmail"
                            name="email"
                            placeholder="example@example.com"
                            type="email"
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="examplePassword">
                            Password
                        </Label>
                        <Input
                            id="examplePassword"
                            name="password"
                            placeholder="password"
                            type="password"
                        />
                    </FormGroup>
                    <Button className="login-btn"
                        type="submit"
                        color="primary"
                    >
                        Login
                    </Button>
                    <Label className="create-account-label"> <Link to="/signup">Create an account</Link></Label>

                </Form>
                </div>
            </div >
        );
    }
}
export default Login;
