import React, { Component } from 'react'
import './Signup.css';
import { Button, Input, Form, FormText, FormGroup, Label } from 'reactstrap';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

class Signup extends Component {

    state = {
        name: '',
        surname: '',
        email: '',
        password: '',
        valid:false,
        error:''
    }
    onNameChange = (e) => {
        const name = e.target.value;
        this.setState(()=> ({name}))
        this.setState({valid: true})
        console.log(name)
    }
    onSurnameChange = (e) => {
        const surname = e.target.value;
        this.setState(()=> ({surname}))
    }
    onEmailChange = (e) => {
        const email = e.target.value;
        this.setState(()=> ({email}))
    }
    onPasswordChange = (e) => {
        const password = e.target.value;
        this.setState(()=> ({password}))
    }

    onSubmit= (e) =>{
        e.preventDefault();

        if (!this.state.name || !this.state.surname||!this.state.email||!this.state.password ) {
            this.setState({valid: false})
            this.setState({error: "Lütfen tüm alanları doldurunuz..."})
        }

        
    }


    render() {
        return (
            <div>
                <div className="signup-container">
                    <div className="form-content">
                        <h1>Signup</h1>
                        {this.state.error && <p>{this.state.error}</p>}
                        <Form onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Label for="exampleName">
                                    Name
                                </Label>
                                <Input 
                                    valid={this.state.valid}
                                    id="exampleName"
                                    name="name"
                                    placeholder="Name"
                                    type="name"
                                    value={this.state.name}
                                    onChange={this.onNameChange}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleSurname">
                                    Surname
                                </Label>
                                <Input
                                    id="exampleSurname"
                                    name="surname"
                                    placeholder="Surname"
                                    type="surname"
                                    value={this.state.surname}
                                    onChange={this.onSurnameChange}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail">
                                    Email
                                </Label>
                                <Input
                                    id="exampleEmail"
                                    name="email"
                                    placeholder="example@example.com"
                                    type="email"
                                    value={this.state.email}
                                    onChange={this.onEmailChange}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="examplePassword">
                                    Password
                                </Label>
                                <Input
                                    id="examplePassword"
                                    name="password"
                                    placeholder="password"
                                    type="password"
                                    value={this.state.password}
                                    onChange={this.onPasswordChange}
                                />
                            </FormGroup>
                            <Button className="login-btn"
                                type="submit"
                                color="primary"
                            >
                                Register
                            </Button>
                            <Label className="login-account-label"> <Link to="/">Already have an account?</Link></Label>

                        </Form>
                    </div>
                </div >
            </div>
        )
    }
}
export default Signup;
